import { VercelRequest, VercelResponse } from '@vercel/node';
import { z } from 'zod';

const adminLoginSchema = z.object({
  username: z.string(),
  password: z.string(),
});

export default async function handler(req: VercelRequest, res: VercelResponse) {
  if (req.method !== 'POST') {
    return res.status(405).json({ success: false, message: 'Method not allowed' });
  }

  try {
    const { username, password } = adminLoginSchema.parse(req.body);

    // Simple hardcoded admin credentials
    if (username === 'SonuHoney' && password === 'Chipmunk@15#') {
      res.json({ success: true, message: 'Login successful' });
    } else {
      res.status(401).json({ success: false, message: 'Invalid credentials' });
    }
  } catch (error) {
    console.error('Admin login error:', error);
    res.status(400).json({ 
      success: false, 
      message: error instanceof Error ? error.message : 'Login failed' 
    });
  }
}

api/getConversation.ts.content:
import { VercelRequest, VercelResponse } from '@vercel/node';
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseAdmin = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL || '',
  process.env.SUPABASE_SERVICE_ROLE_KEY || ''
);

export default async function handler(req: VercelRequest, res: VercelResponse) {
  if (req.method !== 'GET') {
    return res.status(405).json({ success: false, message: 'Method not allowed' });
  }

  try {
    const { hugid } = req.query;

    if (!hugid) {
      return res.status(400).json({ success: false, message: 'hugid required' });
    }

    // Get the hug
    const { data: hug, error: hugError } = await supabaseAdmin
      .from('written hug')
      .select('*')
      .eq('id', hugid)
      .single();

    if (hugError) throw hugError;

    // Get replies (note: table name has space)
    const { data: replies, error: repliesError } = await supabaseAdmin
      .from('hug replies')
      .select('*')
      .eq('hugid', hugid)
      .order('created_at', { ascending: true });

    if (repliesError) throw repliesError;

    res.json({ success: true, hug, replies });
  } catch (error) {
    console.error('Get conversation error:', error);
    res.status(500).json({ 
      success: false, 
      message: error instanceof Error ? error.message : 'Failed to fetch conversation' 
    });
  }
}

