/* Glass effect for premium navbar */
.glass-premium {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Times New Roman italic style for hero subtitle */
.times-new-roman-italic {
  font-family: 'Times New Roman', Times, serif;
  font-style: italic;
}

/* Premium smooth scrolling with easing */
html {
  scroll-behavior: smooth;
}

.premium-scroll {
  scroll-behavior: smooth;
}

/* Premium scrollbar with smooth animations */
::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: linear-gradient(180deg, hsl(var(--muted)), hsl(var(--muted)/0.5));
  border-radius: 10px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, hsl(var(--primary)), hsl(var(--primary)/0.7));
  border-radius: 10px;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, hsl(var(--primary)), hsl(var(--primary)/0.9));
