import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { Heart, Send } from 'lucide-react';

interface FormData {
  name: string;
  email: string;
  phone: string;
  recipientName: string;
  serviceType: string;
  deliveryType: string;
  feelings: string;
  story: string;
  specificDetails: string;
}

const ContactForm = () => {
  const { toast } = useToast();
  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    phone: '',
    recipientName: '',
    serviceType: '',
    deliveryType: '',
    feelings: '',
    story: '',
    specificDetails: ''
  });

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await fetch("/api/submitHug", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (result.success) {
        toast({
          title: "Message Sent! â¤ï¸",
          description: "Thank you for sharing your story. We'll reach out within 24 hours."
        });

        // reset form
        setFormData({
          name: '',
          email: '',
          phone: '',
          recipientName: '',
          serviceType: '',
          deliveryType: '',
          feelings: '',
          story: '',
          specificDetails: ''
        });
      } else {
        toast({
          title: "Failed to Send",
          description: result.message || "Please try again later.",
          variant: "destructive"
        });
      }
    } catch (error) {
      toast({
        title: "Network Error",
        description: "Please check your connection.",
        variant: "destructive"
      });
    }
  };

  const serviceTypes = [
    'Love Letter', 'Gratitude Message', 'Apology Letter',
    'Birthday Message', 'Anniversary Letter', 'Thank You Note',
    'Friendship Letter', 'Family Message', 'Custom Request'
  ];

  return (
    <Card className="max-w-4xl mx-auto shadow-2xl border-0 bg-gradient-to-br from-background to-muted/30">
      <CardHeader className="text-center pb-8">
        <CardTitle className="text-3xl font-bold flex items-center justify-center gap-3">
          <Heart className="h-8 w-8 text-primary" />
          Share Your Heart
        </CardTitle>
        <p className="text-muted-foreground mt-2">
          Tell us your story, and we'll help you express it beautifully
        </p>
      </CardHeader>

      <CardContent className="p-8">
        <form onSubmit={handleSubmit} className="space-y-8">
          {/* No action or method attributes here */}

          <div className="space-y-6">
            <h3 className="text-xl font-semibold text-primary">Personal Information</h3>
            <div className="grid md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <Label>Your Name *</Label>
                <Input name="name" required value={formData.name} onChange={handleInputChange} />
              </div>
              <div className="space-y-2">
                <Label>Email Address *</Label>
                <Input name="email" type="email" required value={formData.email} onChange={handleInputChange} />
              </div>
            </div>
            <div className="space-y-2">
              <Label>Phone Number *</Label>
              <Input name="phone" type="tel" required value={formData.phone} onChange={handleInputChange} />
            </div>
          </div>

          <div className="space-y-6">
            <h3 className="text-xl font-semibold text-primary">Message Details</h3>
            <div className="grid md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <Label>Recipient's Name *</Label>
                <Input name="recipientName" required value={formData.recipientName} onChange={handleInputChange} />
              </div>
              <div className="space-y-2">
                <Label>Type of Message *</Label>
                <select
                  name="serviceType"
                  required
                  value={formData.serviceType}
                  onChange={handleInputChange}
                  className="w-full h-12 px-3 py-2 border border-input bg-background rounded-md text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2"
                >
                  <option value="">Select type</option>
                  {serviceTypes.map(type => <option key={type}>{type}</option>)}
                </select>
              </div>
            </div>
            <div className="space-y-2">
              <Label>Delivery Type *</Label>
              <select
                name="deliveryType"
                required
                value={formData.deliveryType}
                onChange={handleInputChange}
                className="w-full h-12 px-3 py-2 border border-input bg-background rounded-md text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2"
              >
                <option value="">Select delivery type</option>
                <option value="Standard Delivery">Standard Delivery (10 days after dispatch)</option>
                <option value="Express Delivery">Express Delivery (2-3 days after dispatch) - â‚¹150 extra</option>
              </select>
            </div>
          </div>

          <div className="space-y-6">
            <h3 className="text-xl font-semibold text-primary">Your Story</h3>
            <div className="space-y-2">
              <Label>Feelings *</Label>
              <Textarea name="feelings" required value={formData.feelings} onChange={handleInputChange} />
            </div>
            <div className="space-y-2">
              <Label>Story *</Label>
              <Textarea name="story" required value={formData.story} onChange={handleInputChange} />
            </div>
            <div className="space-y-2">
              <Label>Specific Details</Label>
              <Textarea name="specificDetails" value={formData.specificDetails} onChange={handleInputChange} />
            </div>
          </div>

          <div className="p-6 bg-muted/50 rounded-lg">
            <h4 className="font-semibold mb-2 text-primary">Delivery Information</h4>
            <p className="text-sm text-muted-foreground">
              â€¢ Delivery all over India<br />
              â€¢ Timeline: 10-15 days<br />
              â€¢ Contact: onaamikasadguru@gmail.com
            </p>
          </div>

          <Button type="submit" className="w-full h-14 text-lg bg-gradient-to-r from-primary to-purple-600">
            <div className="flex items-center gap-3">
              <Send className="h-5 w-5" />
              Send My Story
            </div>
          </Button>
        </form>
      </CardContent>
    </Card>
  );
};

export default ContactForm;

client/src/components/FAQSection.tsx.content:
import React from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Plus, Minus } from 'lucide-react';
import { useState } from 'react';

const FAQSection = () => {
  const [openItems, setOpenItems] = useState<number[]>([]);

  const toggleItem = (index: number) => {
    setOpenItems(prev => 
      prev.includes(index) 
        ? prev.filter(i => i !== index)
        : [...prev, index]
    );
  };

  const faqs = [
    {
      question: "Who writes the messages?",
      answer: "A team led by Onaamika Sadguru â€” our Chief Emotions Officer â€” carefully crafts every message with love and authenticity."
    },
    {
      question: "Can I review the message before it's sent?",
      answer: "Absolutely. You'll receive a draft to review, and we'll make changes until it feels just right."
    },
    {
      question: "Can I stay anonymous?",
      answer: "Yes. If you wish, we can deliver the message without revealing your name."
    },
    {
      question: "How long does it take to receive the message?",
      answer: "Standard delivery is within 10â€“15 days. We take time to get every word just right."
    },
    {
      question: "Can I send messages for any occasion?",
      answer: "Yes! Whether it's love, apology, celebration, or just because â€” we help you say it from the heart."
    },
    {
      question: "Is this service available outside India?",
      answer: "Currently, we serve all corners of India. International delivery coming soon!"
    }
  ];

  return (
    <section className="py-32 px-6 bg-gradient-to-b from-background to-muted/20 relative overflow-hidden">
      <div className="max-w-4xl mx-auto relative z-10">
        <div className="text-center mb-16">
          <h2 className="text-5xl md:text-6xl font-bold mb-8">
            <span className="bg-gradient-to-r from-primary to-purple-600 bg-clip-text text-transparent">
              You Might Be Wonderingâ€¦
            </span>
          </h2>
        </div>

        <div className="space-y-4">
          {faqs.map((faq, index) => (
            <Card key={index} className="border-0 bg-gradient-to-br from-background to-muted/30 hover:shadow-lg transition-all duration-300">
              <CardContent className="p-0">
                <button
                  onClick={() => toggleItem(index)}
                  className="w-full p-6 text-left flex justify-between items-center hover:bg-muted/20 transition-colors duration-200"
                >
                  <h3 className="text-lg font-semibold pr-4">{faq.question}</h3>
                  {openItems.includes(index) ? (
                    <Minus className="h-5 w-5 text-primary flex-shrink-0" />
                  ) : (
                    <Plus className="h-5 w-5 text-primary flex-shrink-0" />
                  )}
                </button>
                {openItems.includes(index) && (
                  <div className="px-6 pb-6">
                    <p className="text-muted-foreground leading-relaxed">{faq.answer}</p>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FAQSection;

