import { useLocation } from 'wouter';
import backgroundMusic from '@assets/Evare (PenduJatt.Com.Se)-trimmed_1755178220859.mp3';

interface Hug {
  id: string;
  Name: string;
  'Recipient\'s Name': string;
  'Email Address': string;
  'Phone Number': number;
  'Type of Message': string;
  'Message Details': string;
  Feelings: string;
  Story: string;
  'Specific Details': string;
  'Delivery Type': string;
  Status: string;
  Date: string;
}

const AdminDashboard = () => {
  const [hugs, setHugs] = useState<Hug[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');
  const [unreadCount, setUnreadCount] = useState(0);
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const audioRef = useRef<HTMLAudioElement>(null);

  useEffect(() => {
    fetchHugs();
    fetchUnreadCount();

    // Setup background music
    const setupBackgroundMusic = () => {
      if (audioRef.current) {
        const audio = audioRef.current;
        audio.volume = 0.18;
        audio.loop = true;

        const fadeIn = () => {
          audio.volume = 0;
          audio.play().catch(e => console.log('Audio autoplay prevented:', e));

          const fadeInInterval = setInterval(() => {
            if (audio.volume < 0.18) {
              audio.volume = Math.min(audio.volume + 0.01, 0.18);
            } else {
              clearInterval(fadeInInterval);
            }
          }, 100);
        };

        const startMusic = () => {
          fadeIn();
          document.removeEventListener('click', startMusic);
        };

        document.addEventListener('click', startMusic, { once: true });
      }
    };

    setupBackgroundMusic();

    // Set up periodic refresh for unread count
    const interval = setInterval(fetchUnreadCount, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchHugs = async () => {
    try {
      const response = await fetch('/api/getHugs');
      const result = await response.json();

      if (result.success) {
        setHugs(result.hugs || []);
      } else {
        toast({
          title: "Error",
          description: "Failed to fetch hugs",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Error fetching hugs:', error);
      toast({
        title: "Error",
        description: "Failed to connect to server",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchUnreadCount = async () => {
    try {
      const response = await fetch('/api/getUnreadCount');
      const result = await response.json();
      if (result.success) {
        setUnreadCount(result.unreadCount);
      }
    } catch (error) {
      console.error('Error fetching unread count:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'new': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'replied': return 'bg-green-100 text-green-800 border-green-200';
      case 'client replied': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'in progress': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'completed': return 'bg-purple-100 text-purple-800 border-purple-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
